mapper.update:
update list
set ansnum =
ansnum + 1
where grpno = #{grpno}
and ansnum > #{ansnum}

mapper.select:
select listno, grpno, indent, ansnum, title
from list
where list no = #{listno}

mapper.update:
UPDATE list
mname = #{mname}, title = #{title}, content = #{content},
filename = #{filename}, filesize = #{filesize}
WHERE list no = #{listno}

mapper.select:
SELECT listno,mname, title, content, viewcnt, wdate, filename
FROM list
WHERE listno = #{listno}
